SELECT wno, namekor, nameeng, type, price, poster, nickname, num
FROM (SELECT wno, namekor, nameeng, type, price, poster, nickname, rownum as num
FROM (SELECT w.wno, w.namekor, w.nameeng, w.type, w.price, w.poster, m.nickname
FROM wine w JOIN wine_member m ON w.seller=m.userid
WHERE namekor LIKE '%'||#{fd}||'%'
AND acid=#{acid} 
AND sugar=#{sugar} 
AND body=#{body} 
AND tannin=#{tannin} 

	AND type IN
	
		#{type}
	


	
		AND food LIKE '%'||#{food}||'%'
	


	
		AND aroma LIKE '%'||#{aroma}||'%'
	

AND TO_NUMBER(NVL(REPLACE(REPLACE(price, '원', ''), ',', ''), '30000000'))<= TO_NUMBER(#{price})
ORDER BY wno DESC
))
WHERE num BETWEEN #{start} AND #{end}

